import SwiftUI

struct StatusIndicatorView: View {
    var status: TodoStatus

    var body: some View {
        let backgroundColor: Color = {
            switch status {
            case .completed:
                    return Color.green
            case .pending:
                return Color.orange

            }
        }()

        Text(status == .pending ? "Pending" : "Completed")
            .font(.footnote)
            .foregroundColor(.white)
            .padding(Spacing.s8)
            .background(backgroundColor)
            .clipShape(Capsule())
    }
}

#Preview {
    StatusIndicatorView(status: .pending)
}
